import React from "react";
import styled from "styled-components";
import bgImage from "../assets/signup-bg.png";

export default function Signin() {
  return (
    <Container>
      <Title>회원가입</Title>

      <Form>
        {/* 닉네임 */}
        <InputBox placeholder="닉네임" />

        {/* 출생연도 + 헬퍼 */}
        <div>
          <InputBox placeholder="출생연도" inputMode="numeric" maxLength={4} />
          <Helper>예) 2004</Helper>
        </div>

        {/* 아이디 + 헬퍼 */}
        <div>
          <IdRow>
            <IdInput placeholder="아이디" />
            <CheckButton>중복<br />확인</CheckButton>
          </IdRow>
          <Helper>예) 2004</Helper>
        </div>

        {/* 비밀번호 */}
        <InputBox type="password" placeholder="비밀번호" />

        {/* 비밀번호 확인 + 헬퍼 */}
        <div>
          <InputBox type="password" placeholder="비밀번호 확인" />
          <Helper>예) 2004</Helper>
        </div>
      </Form>
    </Container>
  );
}

/* ===== styled-components ===== */

const Container = styled.div`
  width: 393px;
  height: 852px;
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2.5rem 1.25rem;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 3rem; /* 48px */
  color: #4CAF50;
  margin-top: 3.75rem; /* 60px */
`;

const Form = styled.div`
  width: 17.25rem; /* 276px */
  margin-top: 1.875rem; /* 30px */
  display: flex;
  align-items: center;
  flex-direction: column;
  gap: 1.875rem; /* 30px */
`;

const InputBox = styled.input`
  width: 17.25rem; /* 276px */
  height: 3rem;    /* 48px */
  box-sizing: border-box;
  border: 0.125rem solid #8F8F8F;
  border-radius: 0.625rem; /* 10px */
  font-size: 1rem;
  padding-left: 1rem;
  outline: none;
`;

const Helper = styled.p`
  margin: 0;
  margin-top: 0.625rem; /* 10px */
  font-size: 1rem; /* 16px */
  color: #666;
  text-align: left;
  width: 100%;
`;

const IdRow = styled.div`
  display: flex;
  align-items: center;
  gap: 0.3125rem; /* 5px */
`;

const IdInput = styled(InputBox)`
  width: 221px;
  flex: none;
`;

const CheckButton = styled.button`
  width: 50px;
  height: 3rem;
  box-sizing: border-box;
  background-color: #32885D;
  color: #fff;
  font-size: 0.8125rem; /* 13px */
  border: none;
  border-radius: 0.625rem; /* 10px */
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  line-height: 1.1;
  text-align: center;
`;
