// src/pages/loading.jsx
import React from "react";
import styled from "styled-components";

import bgImage from "../assets/signup-bg.png";
import parkImage from "../assets/공원조아용.png";

export default function LoadingPage() {
  return (
    <Container>
      <CenterImage src={parkImage} alt="공원조아용" />
      <TextGroup>
        <FestivalText>축제를 불러오는 중이야~</FestivalText>
        <LoadingText>Loading....</LoadingText>
      </TextGroup>
    </Container>
  );
}

/* 로그인/회원가입과 동일한 컨테이너 스타일 */
const Container = styled.div`
  width: 393px;
  height: 852px;
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2.5rem 1.25rem; /* 상 40px, 좌우 20px */
  margin: 0 auto;
  justify-content: center;
`;

/* 중앙 이미지 */
const CenterImage = styled.img`
  max-width: 80%;
  height: auto;
  margin-bottom: 2.5rem; /* 40px */
`;

/* 텍스트 묶음 */
const TextGroup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.875rem; 
`;

/* “축제를 불러오는 중이야~” */
const FestivalText = styled.p`
  color: #66ce94;
  font-size: 1.875rem; /* 30px */
  font-family: "TJJoyofsingingB";
`;

/* “Loading....” */
const LoadingText = styled.p`
  color: #32885d;
  font-size: 1.875rem; /* 30px */
  font-family: "TJJoyofsingingB";
`;
