// src/pages/modals/namemodal.jsx
import React from "react";
import styled from "styled-components";
import yong from "../../assets/연필조아용.png"; // 연필조아용 이미지

export default function NameModal() {
  return (
    <Stage>
      <Canvas>
        <Card>
          <Mascot src={yong} alt="yong" />
          <Title aria-hidden>닉네임 변경 모달</Title>

          <Field>
            <VisuallyHidden htmlFor="nickname">변경할 닉네임</VisuallyHidden>
            <Input id="nickname" placeholder="변경할 닉네임" />
          </Field>

          <Buttons>
            <Btn type="button" variant="ghost">취소</Btn>
            <Btn type="button">변경하기</Btn>
          </Buttons>
        </Card>
      </Canvas>
    </Stage>
  );
}

/* ===== Layout ===== */
const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  justify-content: center;
  background: #e5a0a5; /* 피그마 배경색 */
`;

const Canvas = styled.div`
  width: 53.25rem; /* 852px */
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Card = styled.div`
  position: relative;
  width: 44rem; /* 704px */
  padding: 3rem 3rem 2.25rem;
  background: #ffffff;
  border-radius: 2rem;
  box-shadow: 0 0.75rem 2rem rgba(0, 0, 0, 0.15);
`;

/* ===== Mascot 위치 수정 ===== */
const Mascot = styled.img`
  position: absolute;
  top: -5rem;   /* 카드보다 위로 올라오게 */
  right: 4.5rem; /* 카드 오른쪽에서 조금 안쪽 */
  width: 8.5rem; /* 크기 살짝 키움 */
  height: auto;
  user-select: none;
  pointer-events: none;
`;

const Title = styled.h2`
  margin: 0 0 1.25rem;
  font-size: 1rem;
  font-weight: 600;
  color: #6b6b6b;
`;

const Field = styled.div`
  margin-top: 2.5rem;
`;

const Input = styled.input`
  width: 100%;
  height: 4rem;
  padding: 0 1.5rem;
  font-size: 1.125rem;
  border: 0.125rem solid #9aa0a6;
  border-radius: 1.5rem;
  outline: none;
  ::placeholder { color: #bdbdbd; }
  :focus {
    border-color: #7a8a7f;
    box-shadow: 0 0 0 0.25rem rgba(63, 126, 91, 0.12);
  }
`;

const Buttons = styled.div`
  display: flex;
  gap: 1.25rem;
  margin-top: 2rem;
`;

const Btn = styled.button`
  flex: 1;
  height: 3.5rem;
  border: none;
  border-radius: 2rem;
  font-size: 1.125rem;
  font-weight: 700;
  box-shadow: 0 0.5rem 1rem rgba(0,0,0,0.08);
  cursor: pointer;
  transition: transform 0.05s ease, box-shadow 0.2s ease, opacity 0.2s ease;

  background: ${({ variant }) => (variant === "ghost" ? "#eeeeee" : "#3f7e5b")};
  color: ${({ variant }) => (variant === "ghost" ? "#2f6a4a" : "#ffffff")};

  :active { transform: translateY(0.0625rem); }
`;

const VisuallyHidden = styled.label`
  position: absolute;
  width: 1px; height: 1px; padding: 0; margin: -1px;
  overflow: hidden; clip: rect(0 0 0 0); white-space: nowrap; border: 0;
`;
