// src/pages/modals/passwordmodal.jsx
import React, { useMemo, useState } from "react";
import styled, { createGlobalStyle } from "styled-components";

// ===== 폰트 & 이미지 =====
import JoyM from "../../fonts/TJJoyofsingingM_TTF.ttf"; // Medium
import JoyB from "../../fonts/TJJoyofsingingB_TTF.ttf"; // Bold
import mascot from "../../assets/연필조아용.png";        // 마스코트

// ===== 전역 폰트 주입 =====
const GlobalFonts = createGlobalStyle`
  @font-face { font-family:"TJJoyofsingingM"; src:url(${JoyM}) format("truetype"); font-weight:500; font-display:swap; }
  @font-face { font-family:"TJJoyofsingingB"; src:url(${JoyB}) format("truetype"); font-weight:700; font-display:swap; }
`;

// ===== 비밀번호 변경 모달 =====
// savedPassword는 실제 서버에서 받아온 현재 비밀번호(또는 검증 API)와 대조하는 용도
export default function PasswordModal({ savedPassword = "1234" /* 데모용. 실제 서비스에선 제거/API로 대체 */ }) {
  const [currentPw, setCurrentPw]   = useState("");
  const [newPw, setNewPw]           = useState("");
  const [confirmPw, setConfirmPw]   = useState("");

  // 기존 비밀번호 체크 (입력 즉시 하단 문구 표시)
  const isCurrentMatch = useMemo(() => currentPw.length > 0 && currentPw === savedPassword, [currentPw, savedPassword]);
  const showCurrentMsg = currentPw.length > 0;

  // 새 비밀번호 일치 체크 (입력 즉시 하단 문구 표시)
  const isPwMatched   = useMemo(() => confirmPw.length > 0 && newPw === confirmPw, [newPw, confirmPw]);
  const showMatchMsg  = confirmPw.length > 0;

  return (
    <>
      <GlobalFonts />
      <Wrap>
        <Card role="dialog" aria-label="비밀번호 변경 모달">
          <Mascot src={mascot} alt="연필조아용" />

          {/* --- 기존 비밀번호 --- */}
          <InputBox>
            <Input
              type="password"
              placeholder="기존 비밀번호"
              value={currentPw}
              onChange={(e) => setCurrentPw(e.target.value)}
            />
          </InputBox>

          {/* 결과 문구 (입력하면 아래에 표시) */}
          {showCurrentMsg && (
            <Hint $ok={isCurrentMatch}>
              {isCurrentMatch ? "기존 비밀번호가 맞아요!" : "기존 비밀번호와 동일하지 않아요!"}
            </Hint>
          )}

          {/* --- 새 비밀번호 (위 폼과 간격: 30px) --- */}
          <InputBox style={{ marginTop: "1.875rem" /* 30px */ }}>
            <Input
              type="password"
              placeholder="변경할 비밀번호"
              value={newPw}
              onChange={(e) => setNewPw(e.target.value)}
            />
          </InputBox>

          {/* --- 비밀번호 확인 (새 비번과 간격: 20px) --- */}
          <InputBox style={{ marginTop: "1.25rem" /* 20px */ }}>
            <Input
              type="password"
              placeholder="비밀번호 확인"
              value={confirmPw}
              onChange={(e) => setConfirmPw(e.target.value)}
            />
          </InputBox>

          {/* 결과 문구 (입력하면 아래에 표시) */}
          {showMatchMsg && (
            <Hint $ok={isPwMatched}>
              {isPwMatched ? "비밀번호가 일치합니다!" : "비밀번호가 일치하지 않아요!"}
            </Hint>
          )}

          {/* 버튼 */}
          <Buttons>
            <Button data-variant="cancel">취소</Button>
            <Button data-variant="confirm">변경하기</Button>
          </Buttons>
        </Card>
      </Wrap>
    </>
  );
}

/* ===== 외부 컨테이너 (중앙 정렬 + 상단 여유) =====
   width: 21.875rem (350px)
   margin-top: 7.5rem (120px) — 마스코트가 위로 나와도 안 잘리게
*/
const Wrap = styled.div`
  width: 21.875rem; /* 350px */
  margin: 7.5rem auto 0; /* 120px */
  overflow: visible;
`;

/* ===== 카드(흰 배경) =====
   width: 21.875rem (350px), height: auto
   border-radius: 1.25rem (20px)
   padding: 위 4rem(64px) 좌우 1.25rem(20px) 아래 1.25rem(20px)
*/
const Card = styled.div`
  position: relative;
  width: 21.875rem; /* 350px */
  background: #fff;
  border-radius: 1.25rem; /* 20px */
  box-shadow: 0 0.25rem 0.75rem rgba(0,0,0,.15);
  padding: 4rem 1.25rem 1.25rem; /* 64px 20px 20px */
`;

/* ===== 마스코트 (위치/크기 튜닝 포인트) =====
   top: -2.75rem(-44px)  width: 7.5rem(120px)
*/
const Mascot = styled.img`
  position: absolute;
  top: -2.75rem; /* -44px */
  left: 50%;
  transform: translateX(-50%);
  width: 7.5rem; /* 120px */
  height: auto;
  pointer-events: none;
  user-select: none;
`;

/* ===== 인풋 래퍼 ===== */
const InputBox = styled.div`
  width: 100%;
`;

/* ===== 인풋 =====
   height: 2.5rem(40px)  radius: 0.625rem(10px)  border: 0.125rem(2px)
*/
const Input = styled.input`
  width: 100%;
  height: 2.5rem; /* 40px */
  padding: 0 0.75rem; /* 12px */
  border: 0.125rem solid #9aa0a6; /* 2px */
  border-radius: 0.625rem; /* 10px */
  font-family: "TJJoyofsingingM", sans-serif;
  font-weight: 500;
  font-size: 0.875rem; /* 14px */
  ::placeholder { color: #bdbdbd; }
  :focus { outline: none; border-color: #32885d; }
`;

/* ===== 검증 문구 =====
   초록(#00CA98) / 빨강(#FF5656)
*/
const Hint = styled.p`
  margin: 0.5rem 0 0 0.25rem; /* 8px 0 0 4px */
  font-size: 0.875rem; /* 14px */
  font-family: "TJJoyofsingingM", sans-serif;
  color: ${({ $ok }) => ($ok ? "#00CA98" : "#FF5656")};
`;

/* ===== 버튼 영역 ===== */
const Buttons = styled.div`
  display: flex;
  justify-content: center;
  gap: 0.75rem; /* 12px */
  margin-top: 1.25rem; /* 20px */
`;

/* ===== 버튼 =====
   7.5rem×2.5rem (120×40)  radius: 1.25rem(20px)
   취소: #E8E8E8 / #32885D,  변경: #32885D / #fff
*/
const Button = styled.button`
  width: 7.5rem;  /* 120px */
  height: 2.5rem; /* 40px */
  border: none;
  border-radius: 1.25rem; /* 20px */
  font-size: 0.875rem; /* 14px */
  font-family: "TJJoyofsingingB", sans-serif;
  font-weight: 700;
  cursor: pointer;

  &[data-variant="cancel"] { background:#E8E8E8; color:#32885D; }
  &[data-variant="confirm"]{ background:#32885D; color:#fff; }
`;
