// src/pages/Loading/index.jsx
import React from "react";
import styled from "styled-components";
import bgImage from "../../assets/signup-bg.png"; // 로그인/회원가입 배경
import parkImage from "../../assets/공원조아용.png"; // 중앙 이미지

export default function LoadingPage() {
  return (
    <Wrapper>
      <Content>
        <CenterImage src={parkImage} alt="공원조아용" />
        <TextGroup>
          <FestivalText>축제를 불러오는 중이야~</FestivalText>
          <LoadingText>Loading....</LoadingText>
        </TextGroup>
      </Content>
    </Wrapper>
  );
}

// ===== 스타일드 컴포넌트 =====
const Wrapper = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url(${bgImage});
  background-size: cover;
  background-position: center;
`;

const Content = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const CenterImage = styled.img`
  width: auto;
  height: auto;
  max-width: 80%;
  margin-bottom: 2.5rem; /* 40px */
`;

const TextGroup = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.875rem; /* 30px */
`;

const FestivalText = styled.p`
  color: #66ce94;
  font-size: 1.875rem; /* 30px */
  font-family: "TJJoyofsingingB"; /* B 폰트 */
`;

const LoadingText = styled.p`
  color: #32885d;
  font-size: 1.875rem; /* 30px */
  font-family: "TJJoyofsingingB"; /* B 폰트 */
`;
