// src/pages/modals/logoutmodal.jsx
import React from "react";
import styled, { createGlobalStyle } from "styled-components";

// ===== 폰트 & 이미지 =====
import JoyB from "../../fonts/TJJoyofsingingB_TTF.ttf"; // Bold
import mascot from "../../assets/연필조아용.png";        // 마스코트

// ===== 전역 폰트 주입 =====
const GlobalFonts = createGlobalStyle`
  @font-face {
    font-family: "TJJoyofsingingB";
    src: url(${JoyB}) format("truetype");
    font-weight: 700;
    font-display: swap;
  }
`;

export default function LogoutModal() {
  return (
    <>
      <GlobalFonts />
      <Wrap>
        <Card>
          <Mascot src={mascot} alt="연필조아용" />
          <Message>정말 로그아웃 하시겠어요?</Message>
          <Buttons>
            <Button data-variant="cancel">취소</Button>
            <Button data-variant="confirm">Logout</Button>
          </Buttons>
        </Card>
      </Wrap>
    </>
  );
}

/* === 중앙 정렬 + 상단 여유 === */
const Wrap = styled.div`
  width: 18.75rem; /* 300px */
  margin: 6.875rem auto 0; /* 110px */
  overflow: visible;
`;

/* === 카드 (300×180) === */
const Card = styled.div`
  position: relative;
  width: 18.75rem; /* 300px */
  height: 11.25rem; /* 180px */
  background: #fff;
  border-radius: 1.25rem; /* 20px */
  box-shadow: 0 0.25rem 0.75rem rgba(0,0,0,.15);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 3.5rem; /* 56px */
`;

/* === 마스코트 === */
const Mascot = styled.img`
  position: absolute;
  top: -2.8125rem; /* -45px */
  left: 50%;
  transform: translateX(-50%);
  width: 9.375rem; /* 150px */
  height: auto;
  pointer-events: none;
  user-select: none;
`;

/* === 가운데 메시지 === */
const Message = styled.p`
  color: #32885d;
  font-family: "TJJoyofsingingB", sans-serif;
  font-weight: 700;
  font-size: 1rem; /* 16px */
  margin-top: auto;
  margin-bottom: auto;
`;

/* === 버튼 영역 === */
const Buttons = styled.div`
  display: flex;
  gap: 0.75rem; /* 12px */
  margin-bottom: 1rem; /* 16px */
`;

/* === 버튼 === */
const Button = styled.button`
  width: 7.5rem; /* 120px */
  height: 2.5rem; /* 40px */
  border: none;
  border-radius: 1.25rem; /* 20px */
  font-size: 0.875rem; /* 14px */
  font-family: "TJJoyofsingingB", sans-serif;
  font-weight: 700;
  cursor: pointer;

  &[data-variant="cancel"] {
    background: #e8e8e8;
    color: #32885d;
  }
  &[data-variant="confirm"] {
    background: #32885d;
    color: #fff;
  }
`;
