// src/pages/modals/namemodal.jsx
import React, { useEffect, useState } from "react";
import styled from "styled-components";
import pencilYong from "../../assets/연필조아용.png";

export default function NameModal({
  open,
  onClose,
  onConfirm,
  initialNickname = "",
}) {
  const [nickname, setNickname] = useState(initialNickname);

  useEffect(() => {
    if (!open) return;
    setNickname(initialNickname);
    const onKey = (e) => e.key === "Escape" && onClose?.();
    document.addEventListener("keydown", onKey);
    const prev = document.body.style.overflow;
    document.body.style.overflow = "hidden";
    return () => {
      document.removeEventListener("keydown", onKey);
      document.body.style.overflow = prev;
    };
  }, [open, initialNickname, onClose]);

  if (!open) return null;

  const overlayClick = (e) => {
    if (e.target === e.currentTarget) onClose?.();
  };

  const handleConfirm = () => {
    onConfirm?.(nickname.trim());
    onClose?.();
  };

  return (
    <Overlay onMouseDown={overlayClick}>
      <Card onMouseDown={(e) => e.stopPropagation()} role="dialog" aria-modal="true">
        <Mascot>
          <img src={pencilYong} alt="연필 조아용" />
        </Mascot>

        <Title>닉네임 변경</Title>

        <Input
          autoFocus
          placeholder="변경할 닉네임"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
          maxLength={20}
        />

        <Buttons>
          <Btn $cancel onClick={onClose}>취소</Btn>
          <Btn onClick={handleConfirm}>변경하기</Btn>
        </Buttons>
      </Card>
    </Overlay>
  );
}

/* ===== styled-components ===== */

const Overlay = styled.div`
  position: fixed; inset: 0;
  display: flex; align-items: center; justify-content: center;
  background: rgba(0,0,0,0.35);
  z-index: 1000;
`;

const Card = styled.div`
  width: 300px; height: 228px;
  background: #fff; border-radius: 10px;
  position: relative;
  padding: 56px 20px 20px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.15);
  display: flex; flex-direction: column; align-items: center;

  /* 기본 폰트: 폼 텍스트 = JoyM */
  font-family: 'JoyM', system-ui, -apple-system, Segoe UI, Roboto, 'Noto Sans KR', sans-serif;
`;

const Mascot = styled.div`
  position: absolute; top: -38px; left: 50%; transform: translateX(-50%);
  width: 86px; height: 86px;
  img { width: 100%; height: 100%; object-fit: contain; }
`;

const Title = styled.h3`
  margin: 0 0 14px 0;
  font-size: 16px;
  /* 제목은 Bold 폰트 */
  font-family: 'JoyB', 'JoyM', sans-serif;
  color: #32885D;
`;

const Input = styled.input`
  width: 240px; height: 40px;
  border: 1px solid #C9D3DC; border-radius: 10px;
  padding: 0 12px;
  font-size: 14px; /* JoyM 적용 (Card에서 상속) */
  outline: none;

  &::placeholder {
    font-family: 'JoyM', sans-serif;
    color: #9aa6b2;
  }

  &:focus {
    border-color: #32885D;
    box-shadow: 0 0 0 3px rgba(50,136,93,0.15);
  }
`;

const Buttons = styled.div`
  margin-top: 18px;
  display: flex; gap: 10px;
`;

const Btn = styled.button`
  width: 120px; height: 40px;
  border-radius: 20px; border: none;
  font-size: 14px;
  /* 버튼은 Bold 폰트 */
  font-family: 'JoyB', 'JoyM', sans-serif;
  font-weight: 800;
  cursor: pointer;

  ${({ $cancel }) =>
    $cancel
      ? `background:#E8E8E8; color:#32885D;`
      : `background:#32885D; color:#FFFFFF;`}
`;
