// src/pages/modals/passwordmodal.jsx
import React, { useMemo, useState } from "react";
import styled, { createGlobalStyle } from "styled-components";
import JoyM from "../../fonts/TJJoyofsingingM_TTF.ttf";
import JoyB from "../../fonts/TJJoyofsingingB_TTF.ttf";
import mascot from "../../assets/연필조아용.png";

const GlobalFonts = createGlobalStyle`
  @font-face { font-family:"TJJoyofsingingM"; src:url(${JoyM}) format("truetype"); font-weight:500; }
  @font-face { font-family:"TJJoyofsingingB"; src:url(${JoyB}) format("truetype"); font-weight:700; }
`;

export default function PasswordModal({ savedPassword = "1234" }) {
  const [currentPw, setCurrentPw] = useState("");
  const [newPw, setNewPw] = useState("");
  const [confirmPw, setConfirmPw] = useState("");

  const isCurrentMatch = useMemo(
    () => currentPw.length > 0 && currentPw === savedPassword,
    [currentPw, savedPassword]
  );
  const showCurrentMsg = currentPw.length > 0;

  const isPwMatched = useMemo(
    () => confirmPw.length > 0 && newPw === confirmPw,
    [newPw, confirmPw]
  );
  const showMatchMsg = confirmPw.length > 0;

  return (
    <>
      <GlobalFonts />
      <Wrap>
        <Card>
          <Mascot src={mascot} alt="연필조아용" />

          <InputBox>
            <Input
              type="password"
              placeholder="기존 비밀번호"
              value={currentPw}
              onChange={(e) => setCurrentPw(e.target.value)}
            />
          </InputBox>

          {showCurrentMsg && (
            <Hint $ok={isCurrentMatch}>
              {isCurrentMatch
                ? "기존 비밀번호가 맞아요!"
                : "기존 비밀번호와 동일하지 않아요!"}
            </Hint>
          )}

          <InputBox style={{ marginTop: "1.875rem" /* 30px */ }}>
            <Input
              type="password"
              placeholder="변경할 비밀번호"
              value={newPw}
              onChange={(e) => setNewPw(e.target.value)}
            />
          </InputBox>

          <InputBox style={{ marginTop: "1.25rem" /* 20px */ }}>
            <Input
              type="password"
              placeholder="비밀번호 확인"
              value={confirmPw}
              onChange={(e) => setConfirmPw(e.target.value)}
            />
          </InputBox>

          {showMatchMsg && (
            <Hint $ok={isPwMatched}>
              {isPwMatched
                ? "비밀번호가 일치합니다!"
                : "비밀번호가 일치하지 않아요!"}
            </Hint>
          )}

          <Buttons>
            <Button data-variant="cancel">취소</Button>
            <Button data-variant="confirm">변경하기</Button>
          </Buttons>
        </Card>
      </Wrap>
    </>
  );
}

/* === 중앙 정렬 + 상단 여유 === */
const Wrap = styled.div`
  width: 18.75rem; /* 300px */
  margin: 6.875rem auto 0; /* 110px */
  overflow: visible;
`;

/* === 카드 === */
const Card = styled.div`
  position: relative;
  width: 18.75rem; /* 300px */
  background: #fff;
  border-radius: 1.25rem; /* 20px */
  box-shadow: 0 0.25rem 0.75rem rgba(0,0,0,.15);
  padding: 4.125rem 1.25rem 1.25rem; /* top 66px, 기존보다 10px ↑ */
`;


/* === 마스코트 (닉네임 모달 동일) === */
const Mascot = styled.img`
  position: absolute;
  top: -2.8125rem; /* -45px */
  left: 50%;
  transform: translateX(-50%);
  width: 9.375rem; /* 150px */
  height: auto;
  pointer-events: none;
  user-select: none;
`;

/* === 인풋 래퍼 === */
const InputBox = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
`;

/* === 인풋 (닉네임 모달 동일 너비) === */
const Input = styled.input`
  width: 15rem; /* 240px */
  height: 2.5rem; /* 40px */
  padding: 0 0.75rem; /* 12px */
  border: 0.125rem solid #9aa0a6; /* 2px */
  border-radius: 0.625rem; /* 10px */
  font-family: "TJJoyofsingingM", sans-serif;
  font-weight: 500;
  font-size: 0.875rem; /* 14px */
  ::placeholder { color: #bdbdbd; }
  :focus { outline: none; border-color: #32885d; }
`;

/* === 메시지 === */
const Hint = styled.p`
  margin: 0.5rem 0 0 0.25rem; /* 8px 0 0 4px */
  font-size: 0.875rem; /* 14px */
  font-family: "TJJoyofsingingM", sans-serif;
  color: ${({ $ok }) => ($ok ? "#00CA98" : "#FF5656")};
`;

/* === 버튼 영역 === */
const Buttons = styled.div`
  display: flex;
  justify-content: center;
  gap: 0.75rem; /* 12px */
  margin-top: 1.25rem; /* 20px */
`;

/* === 버튼 === */
const Button = styled.button`
  width: 7.5rem; /* 120px */
  height: 2.5rem; /* 40px */
  border: none;
  border-radius: 1.25rem; /* 20px */
  font-size: 0.875rem; /* 14px */
  font-family: "TJJoyofsingingB", sans-serif;
  font-weight: 700;
  cursor: pointer;

  &[data-variant="cancel"] { background:#E8E8E8; color:#32885D; }
  &[data-variant="confirm"]{ background:#32885D; color:#fff; }
`;
