// src/pages/login.jsx
import React from "react";
import styled from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png";
import cloud from "../assets/구름.png";        // 큰 구름
import miniCloud from "../assets/미니구름.png"; // 작은 구름
import titleImg from "../assets/요기용인.png";  // 타이틀 이미지

export default function Login() {
  return (
    <Stage>
      <Container>
        {/* 타이틀 */}
        <Title src={titleImg} alt="요기용in" />

        <Content>
          {/* 로그인 폼 자리 */}
        </Content>

        {/* 구름 2개 */}
        <Cloud src={cloud} alt="구름" />
        <MiniCloud src={miniCloud} alt="미니 구름" />

        {/* 용 캐릭터 */}
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Container = styled.div`
  position: relative;
  width: 24.5625rem;   /* 393px */
  height: 53.25rem;    /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  padding: 2.5rem 1.25rem;
  overflow: hidden;
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
`;

/* 타이틀 - 상단 중앙 */
const Title = styled.img`
  position: absolute;
  top: 8rem;                      /* 위 여백 (시안 맞춰 조정 가능) */
  left: 50%;
  transform: translateX(-50%);
  width: 18rem;                   /* 시안 크기에 맞게 조정 */
  height: auto;
  z-index: 3;
  pointer-events: none;
`;

/* 큰 구름 - 왼쪽 하단 */
const Cloud = styled.img`
  position: absolute;
  left: -1rem;     
  bottom: 0rem;    
  width: 30rem;    
  z-index: 1;
`;

/* 작은 구름 - 오른쪽 하단 */
const MiniCloud = styled.img`
  position: absolute;
  right: 0.5rem;
  bottom: 0rem;
  width: 13rem;    
  z-index: 1;
`;

/* 용 캐릭터 */
const Yong = styled.img`
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  height: auto;
  z-index: 2;
`;
