import React, { useState } from "react";
import styled from "styled-components";
import pencilYong from "../../assets/연필조아용.png";

export default function NameModal({ open, onClose, onConfirm, initialNickname = "" }) {
  const [nickname, setNickname] = useState(initialNickname);

  if (!open) return null;

  return (
    <Overlay>
      <Card>
        <Mascot>
          <img src={pencilYong} alt="연필 조아용" />
        </Mascot>
        <Title>닉네임 변경</Title>
        <Input
          placeholder="변경할 닉네임"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
        />
        <ButtonRow>
          <Btn $cancel onClick={onClose}>취소</Btn>
          <Btn onClick={() => { onConfirm?.(nickname); onClose?.(); }}>변경하기</Btn>
        </ButtonRow>
      </Card>
    </Overlay>
  );
}

const Overlay = styled.div`
  position: fixed;
  inset: 0;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Card = styled.div`
  width: 300px;
  height: 228px;
  background: #fff;
  border-radius: 10px;
  position: relative;
  padding: 56px 20px 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  font-family: 'JoyM', sans-serif; /* 폼 글씨체 */
`;

const Mascot = styled.div`
  position: absolute;
  top: -38px;
  left: 50%;
  transform: translateX(-50%);
  width: 86px;
  height: 86px;
  img {
    width: 100%;
    height: 100%;
    object-fit: contain;
  }
`;

const Title = styled.h3`
  margin-bottom: 14px;
  font-size: 16px;
  font-family: 'JoyB', sans-serif; /* 버튼/제목 글씨체 */
  color: #32885D;
`;

const Input = styled.input`
  width: 240px;
  height: 40px;
  border: 1px solid #C9D3DC;
  border-radius: 10px;
  padding: 0 12px;
  font-size: 14px;
  &:focus {
    border-color: #32885D;
    box-shadow: 0 0 0 3px rgba(50,136,93,0.15);
    outline: none;
  }
`;

const ButtonRow = styled.div`
  margin-top: 18px;
  display: flex;
  gap: 10px;
`;

const Btn = styled.button`
  width: 120px;
  height: 40px;
  border-radius: 20px;
  border: none;
  font-size: 14px;
  font-family: 'JoyB', sans-serif; /* 버튼 글씨체 */
  font-weight: 700;
  cursor: pointer;
  ${({ $cancel }) =>
    $cancel
      ? `background:#E8E8E8; color:#32885D;`
      : `background:#32885D; color:#FFFFFF;`}
`;
