// src/pages/login.jsx
import React from "react";
import styled from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png"; // 캐릭터 이미지

export default function Login() {
  return (
    <Stage>
      <Container>
        {/* 나중에 로그인 폼 들어갈 영역 */}
        <Content>
          {/* input / button 등은 여기에 */}
        </Content>

        {/* 하단 용 캐릭터 */}
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

/* 화면 중앙에 모바일 프레임 고정 */
const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

/* 회원가입 페이지와 동일한 크기/패딩/배경 */
const Container = styled.div`
  position: relative;
  width: 24.5625rem;   /* 393px */
  height: 53.25rem;    /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2.5rem 1.25rem; /* 40px 20px */
  overflow: hidden;         /* 용 이미지가 밖으로 튀어나오지 않게 */
`;

/* 폼은 배경 위, 용보다 위에 오도록 */
const Content = styled.div`
  position: relative;
  z-index: 2;
  width: 100%;
`;

/* 용 캐릭터: 하단 고정, 가로 꽉 채움 */
const Yong = styled.img`
  position: absolute;
  left: 0;
  bottom: 0;         /* 필요시 -0.5rem 등으로 미세 조정 */
  width: 100%;
  height: auto;
  z-index: 1;
  pointer-events: none; /* 클릭 방해 X */
  user-select: none;
`;
