// src/pages/login.jsx
import React from "react";
import styled from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png";
import cloud from "../assets/구름.png";        // 큰 구름
import miniCloud from "../assets/미니구름.png"; // 작은 구름

export default function Login() {
  return (
    <Stage>
      <Container>
        <Content>
          {/* 로그인 폼 자리 */}
        </Content>

        {/* 구름 2개 */}
        <Cloud src={cloud} alt="구름" />
        <MiniCloud src={miniCloud} alt="미니 구름" />

        {/* 용 캐릭터 */}
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Container = styled.div`
  position: relative;
  width: 24.5625rem;   /* 393px */
  height: 53.25rem;    /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  padding: 2.5rem 1.25rem;
  overflow: hidden;
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
`;

/* 큰 구름 - 왼쪽 하단 */
const Cloud = styled.img`
  position: absolute;
  left: -1rem;     /* 시안처럼 약간 바깥으로 */
  bottom: 0rem;    /* 용 뒤쪽으로 */
  width: 30rem;    /* 크기 키움 */
  z-index: 1;
`;

/* 작은 구름 - 오른쪽 하단 */
const MiniCloud = styled.img`
  position: absolute;
  right: 0.5rem;
  bottom: 6.5rem;
  width: 10rem;    /* 크기 키움 */
  z-index: 1;
`;

const Yong = styled.img`
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  height: auto;
  z-index: 2;
`;
