// src/pages/login.jsx
import React from "react";
import styled, { createGlobalStyle } from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png";
import cloud from "../assets/구름.png";        
import miniCloud from "../assets/미니구름.png";
import titleImg from "../assets/요기용인.png";

// 폰트 파일 import
import DNFBitBit from "../fonts/DNFBitBitTTF.ttf";
import JoyM from "../fonts/TJJoyofsingingM_TTF.ttf";

// 전역 폰트 등록
const FontStyles = createGlobalStyle`
  @font-face {
    font-family: 'DNFBitBitTTF';
    src: url(${DNFBitBit}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }

  @font-face {
    font-family: 'JoyM';
    src: url(${JoyM}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }
`;

export default function Login() {
  return (
    <Stage>
      <Container>
        <FontStyles />

        {/* 타이틀 */}
        <Title src={titleImg} alt="요기용in" />

        {/* 중앙 입력 폼 */}
        <Form>
          <InputBox placeholder="아이디를 입력해주세요." />
          <InputBox type="password" placeholder="비밀번호를 입력해주세요." />
        </Form>

        {/* 구름 2개 */}
        <Cloud src={cloud} alt="구름" />
        <MiniCloud src={miniCloud} alt="미니 구름" />

        {/* 용 캐릭터 */}
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Container = styled.div`
  position: relative;
  width: 24.5625rem;   /* 393px */
  height: 53.25rem;    /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  padding: 2.5rem 1.25rem;
  overflow: hidden;
`;

const Title = styled.img`
  position: absolute;
  top: 9rem;
  left: 50%;
  transform: translateX(-50%);
  width: 18rem;
  height: auto;
  z-index: 3;
  pointer-events: none;
`;

const Form = styled.div`
  position: relative;
  z-index: 3;
  margin-top: 15rem; /* 타이틀 아래 여백 */
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.8rem;
`;

const InputBox = styled.input`
  width: 21.5rem;      /* 344px */
  height: 3.75rem;     /* 60px */
  box-sizing: border-box;
  border: 0.125rem solid #8F8F8F;
  border-radius: 0.625rem;
  font-size: 1.125rem; /* 18px */
  padding-left: 1rem;
  outline: none;
  font-family: 'JoyM', sans-serif;

  &::placeholder {
    font-family: 'JoyM', sans-serif;
  }
`;

const Cloud = styled.img`
  position: absolute;
  left: -1rem;     
  bottom: 0rem;    
  width: 30rem;    
  z-index: 1;
`;

const MiniCloud = styled.img`
  position: absolute;
  right: 0.5rem;
  bottom: 0rem;
  width: 13rem;    
  z-index: 1;
`;

const Yong = styled.img`
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  height: auto;
  z-index: 2;
`;
