// src/pages/modals/namemodal.jsx
import React from "react";
import styled, { createGlobalStyle } from "styled-components";

// ===== 폰트 & 이미지 =====
import JoyM from "../../fonts/TJJoyofsingingM_TTF.ttf"; // Medium
import JoyB from "../../fonts/TJJoyofsingingB_TTF.ttf"; // Bold
import mascot from "../../assets/연필조아용.png";        // 연필조아용

/* 전역 폰트 주입: 로딩 페이지 방식과 동일 파일 내에서 처리 */
const GlobalFonts = createGlobalStyle`
  @font-face {
    font-family: "TJJoyofsingingM";
    src: url(${JoyM}) format("truetype");
    font-weight: 500;
    font-style: normal;
    font-display: swap;
  }
  @font-face {
    font-family: "TJJoyofsingingB";
    src: url(${JoyB}) format("truetype");
    font-weight: 700;
    font-style: normal;
    font-display: swap;
  }
`;

export default function NameModal() {
  return (
    <>
      <GlobalFonts />
      <Wrap>
        <Card role="dialog" aria-label="닉네임 변경 모달">
          {/* ▼ 마스코트 위치/크기 조절 포인트: 아래 Mascot 스타일 주석 참고 */}
          <Mascot src={mascot} alt="연필조아용" />
          <FormArea>
            <Input placeholder="변경할 닉네임" />
          </FormArea>
          <Buttons>
            <Button data-variant="cancel">취소</Button>
            <Button data-variant="confirm">변경하기</Button>
          </Buttons>
        </Card>
      </Wrap>
    </>
  );
}

/* 카드 중앙 정렬 + 상단 여유(마스코트가 위로 튀어나와도 안 잘리게) */
const Wrap = styled.div`
  width: 300px;
  margin: 110px auto 0; /* ↑ 부족하면 130~150px로 늘려줘 */
  overflow: visible;     /* 상위 컨테이너에서 자식이 안 잘리도록 */
`;

/* 카드: 300×228, R=10 */
const Card = styled.div`
  position: relative;
  width: 300px;
  height: 180px;
  background: #fff;
  border-radius: 10px;
  box-shadow: 0 4px 12px rgba(0,0,0,.15);
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 56px; /* 마스코트 공간 확보 */
  overflow: visible;
`;

/* ================== 마스코트 튜닝 가이드 ==================
   - top: 음수로 더 작게 → 더 위로 튀어나옴 (잘리면 Wrap의 margin-top도 함께 ↑)
   - width: 크기 조절 (height는 자동 비율)
   - left: 50% + translateX(-50%) → 가로 중앙 고정
   - 미세 위치가 필요하면 top/left 값을 px 단위로 조정
================================================================ */
const Mascot = styled.img`
  position: absolute;
  top: -50px;      /* ▲ 카드 윗선 위로 38px */
  left: 50%;
  transform: translateX(-50%);
  width: 150px;     /* ▲ 마스코트 크기 (필요시 90px 등으로 조정) */
  height: auto;
  pointer-events: none;
  user-select: none;
`;

/* 폼(회원가입 닉네임 폼 느낌): Medium 폰트 */
const FormArea = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Input = styled.input`
  width: 240px;
  height: 40px;
  padding: 0 12px;
  border: 2px solid #9aa0a6;  /* 회원가입 폼 톤 */
  border-radius: 10px;
  font-family: "TJJoyofsingingM", sans-serif; /* M */
  font-weight: 500;
  font-size: 14px;
  ::placeholder { color: #bdbdbd; }
  :focus { outline: none; border-color: #32885d; }
`;

/* 버튼 두 개: 120×40, R=20, 색상 고정 */
const Buttons = styled.div`
  display: flex;
  gap: 12px;
  margin: 12px 0 16px;
`;

const Button = styled.button`
  width: 120px;
  height: 40px;
  border: none;
  border-radius: 20px;
  font-size: 14px;
  font-family: "TJJoyofsingingB", sans-serif; /* B */
  font-weight: 700;
  cursor: pointer;

  &[data-variant="cancel"] {
    background: #E8E8E8; /* 배경 */
    color: #32885D;      /* 글씨 */
  }
  &[data-variant="confirm"] {
    background: #32885D; /* 배경 */
    color: #ffffff;      /* 글씨 */
  }
`;
