// src/pages/signin.jsx
import React, { useState } from "react";
import styled, { createGlobalStyle } from "styled-components";
import TJJoyofsingingM from "../fonts/TJJoyofsingingM_TTF.ttf";
import DNFBitBit from "../fonts/DNFBitBitTTF.ttf";

const GlobalStyle = createGlobalStyle`
  @font-face {
    font-family: 'JoyM';
    src: url(${TJJoyofsingingM}) format('truetype');
    font-weight: normal;
    font-style: normal;
  }
  * {
    font-family: 'JoyM', sans-serif;
  }
`;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const InputBox = styled.input`
  width: 300px;
  height: 50px;
  margin-bottom: 10px;
  padding: 10px;
  font-size: 18px;
  border: 1px solid #ccc;
  border-radius: 8px;
`;

const HelperText = styled.p`
  font-size: 14px;
  margin-top: 10px;
  margin-bottom: 30px;
  color: ${({ color }) => color || "#000"};
`;

const SignUpButton = styled.button`
  @font-face {
    font-family: 'BitBit';
    src: url(${DNFBitBit}) format('truetype');
    font-weight: normal;
    font-style: normal;
  }
  width: 217px;
  height: 58px;
  background-color: #66CE94;
  color: #fff;
  font-size: 32px;
  font-family: 'BitBit', sans-serif; /* 버튼만 다른 폰트 */
  border: none;
  border-radius: 100px;
  cursor: pointer;
`;

export default function Signin() {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [pwCheck, setPwCheck] = useState("");
  const [idMessage, setIdMessage] = useState("");
  const [pwMessage, setPwMessage] = useState("");

  const checkId = () => {
    if (id === "test") {
      setIdMessage({ text: "이미 사용 중입니다!", color: "#FF5656" });
    } else {
      setIdMessage({ text: "사용 가능한 아이디입니다!", color: "#00CA98" });
    }
  };

  const checkPwMatch = () => {
    if (pw && pw === pwCheck) {
      setPwMessage({ text: "비밀번호가 일치합니다!", color: "#00CA98" });
    } else if (pwCheck) {
      setPwMessage({ text: "비밀번호가 일치하지 않습니다!", color: "#FF5656" });
    } else {
      setPwMessage({ text: "", color: "" });
    }
  };

  return (
    <>
      <GlobalStyle />
      <Container>
        <InputBox
          placeholder="아이디"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        {idMessage.text && (
          <HelperText color={idMessage.color}>{idMessage.text}</HelperText>
        )}
        <button onClick={checkId}>중복 확인</button>

        <InputBox
          type="password"
          placeholder="비밀번호"
          value={pw}
          onChange={(e) => setPw(e.target.value)}
        />

        <InputBox
          type="password"
          placeholder="비밀번호 확인"
          value={pwCheck}
          onChange={(e) => {
            setPwCheck(e.target.value);
            checkPwMatch();
          }}
        />
        {pwMessage.text && (
          <HelperText color={pwMessage.color}>{pwMessage.text}</HelperText>
        )}

        <SignUpButton>가입하기</SignUpButton>
      </Container>
    </>
  );
}
