// src/pages/login.jsx
import React from "react";
import styled, { createGlobalStyle } from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png";
import cloud from "../assets/구름.png";        
import miniCloud from "../assets/미니구름.png"; 
import JoyB from "../fonts/TJJoyofsingingB_TTF.ttf";
import JoyM from "../fonts/TJJoyofsingingM_TTF.ttf";

// 전역 폰트 등록
const FontStyles = createGlobalStyle`
  @font-face {
    font-family: 'JoyB';
    src: url(${JoyB}) format('truetype');
    font-weight: bold;
    font-style: normal;
    font-display: swap;
  }
  @font-face {
    font-family: 'JoyM';
    src: url(${JoyM}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }
`;

export default function Login() {
  return (
    <Stage>
      <FontStyles />
      <Container>
        <Content>
          {/* 아이디 */}
          <InputBox placeholder="아이디를 입력해 주세요" />
          {/* 비밀번호 */}
          <InputBox type="password" placeholder="비밀번호를 입력해 주세요" />

          {/* 버튼 영역 */}
          <ButtonWrapper>
            <LoginButton>로그인</LoginButton>
            <SignupButton>회원가입</SignupButton>
          </ButtonWrapper>
        </Content>

        {/* 구름 2개 */}
        <Cloud src={cloud} alt="구름" />
        <MiniCloud src={miniCloud} alt="미니 구름" />

        {/* 용 캐릭터 */}
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

const Stage = styled.div`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Container = styled.div`
  position: relative;
  width: 24.5625rem;   /* 393px */
  height: 53.25rem;    /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  padding: 2.5rem 1.25rem;
  overflow: hidden;
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.25rem; /* 20px - 폼 간격 */
  margin-top: 4rem; /* 타이틀/상단 여백 */
`;

const InputBox = styled.input`
  width: 20rem; /* 320px */
  height: 3rem; /* 48px */
  border: 0.125rem solid #8F8F8F;
  border-radius: 0.625rem;
  font-size: 1rem;
  padding-left: 1rem;
  outline: none;
  font-family: 'JoyM', sans-serif;

  &::placeholder {
    font-family: 'JoyM', sans-serif;
  }
`;

/* 버튼 컨테이너 */
const ButtonWrapper = styled.div`
  display: flex;
  gap: 0.9375rem; /* 15px */
  margin-top: 1.5625rem; /* 25px - 폼과 버튼 간격 */
`;

const LoginButton = styled.button`
  width: 8.125rem;  /* 130px */
  height: 3rem;     /* 48px */
  background-color: #66CE94;
  color: #fff;
  font-size: 1.125rem; /* 18px */
  border: none;
  border-radius: 6.25rem; /* 100px */
  font-family: 'JoyB', sans-serif;
  display: flex;
  align-items: center;
  justify-content: center;
  box-shadow: 2px 2px 0 rgba(0, 0, 0, 0.5);
`;

const SignupButton = styled(LoginButton)`
  background-color: #FCFAF0;
  color: #000;
`;

/* 큰 구름 - 왼쪽 하단 */
const Cloud = styled.img`
  position: absolute;
  left: -1rem;
  bottom: 0rem;
  width: 30rem;
  z-index: 1;
`;

/* 작은 구름 - 오른쪽 하단 */
const MiniCloud = styled.img`
  position: absolute;
  right: 0.5rem;
  bottom: 0rem;
  width: 13rem;
  z-index: 1;
`;

const Yong = styled.img`
  position: absolute;
  left: 0;
  bottom: 0;
  width: 100%;
  height: auto;
  z-index: 2;
`;
