// src/pages/login.jsx
import React from "react";
import styled, { createGlobalStyle } from "styled-components";
import bgImage from "../assets/signup-bg.png";

// 폰트 파일 import
import DNFBitBit from "../fonts/DNFBitBitTTF.ttf";
import JoyM from "../fonts/TJJoyofsingingM_TTF.ttf";

// 전역 폰트 등록
const FontStyles = createGlobalStyle`
  @font-face {
    font-family: 'DNFBitBitTTF';
    src: url(${DNFBitBit}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }

  @font-face {
    font-family: 'JoyM';
    src: url(${JoyM}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }
`;

export default function NicknameBirthForm() {
  return (
    <Container>
      <FontStyles />
      <Form>
        {/* 아이디 입력 */}
        <InputBox placeholder="아이디를 입력해주세요." />

        {/* 비밀번호 입력 */}
        <InputBox type="password" placeholder="비밀번호를 입력해주세요." />
      </Form>
    </Container>
  );
}

/* ===== styled-components ===== */

const Container = styled.div`
  width: 24.5625rem; /* 393px */
  height: 53.25rem; /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  display: flex;
  justify-content: center;
  align-items: center; /* 세로 중앙 정렬 */
  flex-direction: column;
  padding: 2.5rem 1.25rem;
  margin: 0 auto;
`;

const Form = styled.div`
  width: 17.25rem; /* 276px */
  display: flex;
  align-items: center;
  flex-direction: column;
  gap: 1.875rem; /* 30px */
`;

const InputBox = styled.input`
  width: 17.25rem; /* 276px */
  height: 3rem; /* 48px */
  box-sizing: border-box;
  border: 0.125rem solid #8F8F8F; /* 2px */
  border-radius: 0.625rem; /* 10px */
  font-size: 1rem;
  padding-left: 1rem;
  outline: none;
  font-family: 'JoyM', sans-serif;

  &::placeholder {
    font-family: 'JoyM', sans-serif;
  }
`;
