import React, { useState } from "react";
import styled from "styled-components";
import bgImage from "../assets/signup-bg.png";

export default function Signin() {
  // 입력값
  const [nickname, setNickname] = useState("");
  const [birth, setBirth] = useState("");
  const [userId, setUserId] = useState("");
  const [pw, setPw] = useState("");
  const [pw2, setPw2] = useState("");

  // 아이디 중복확인 상태(버튼 눌렀을 때만 메시지 노출)
  const [idChecked, setIdChecked] = useState(false);
  const [idOK, setIdOK] = useState(null); // null | true | false
  const [idMsg, setIdMsg] = useState("");

  // 비밀번호 확인 상태(입력했을 때만 메시지 노출)
  const [pwOK, setPwOK] = useState(null); // null | true | false
  const [pwMsg, setPwMsg] = useState("");

  // 아이디 중복확인 (API 붙이기 전 임시 랜덤)
  const onClickCheckId = () => {
    if (!userId.trim()) {
      setIdChecked(true);
      setIdOK(false);
      setIdMsg("아이디를 입력해주세요.");
      return;
    }
    const ok = Math.random() < 0.5; // UI 테스트용
    setIdChecked(true);
    setIdOK(ok);
    setIdMsg(ok ? "사용 가능한 아이디입니다!" : "이미 사용 중입니다!");
  };

  // 아이디 입력 중이면 결과 초기화
  const onChangeUserId = (v) => {
    setUserId(v);
    setIdChecked(false);
    setIdOK(null);
    setIdMsg("");
  };

  // 비밀번호 일치 여부
  const onChangePw = (v) => {
    setPw(v);
    if (!pw2) { setPwOK(null); setPwMsg(""); return; }
    const same = v === pw2;
    setPwOK(same);
    setPwMsg(same ? "비밀번호가 일치합니다!" : "비밀번호가 일치하지 않습니다.");
  };
  const onChangePw2 = (v) => {
    setPw2(v);
    if (!pw) { setPwOK(null); setPwMsg(""); return; }
    const same = pw === v;
    setPwOK(same);
    setPwMsg(same ? "비밀번호가 일치합니다!" : "비밀번호가 일치하지 않습니다.");
  };

  return (
    <Container>
      <Title>회원가입</Title>

      <Form>
        {/* 닉네임 */}
        <InputBox
          placeholder="닉네임"
          value={nickname}
          onChange={(e) => setNickname(e.target.value)}
        />

        {/* 출생연도 + 고정 헬퍼(예시) */}
        <div>
          <InputBox
            placeholder="출생연도"
            inputMode="numeric"
            maxLength={4}
            value={birth}
            onChange={(e) => setBirth(e.target.value)}
          />
          <Helper>예) 2004</Helper>
        </div>

        {/* 아이디 + 중복확인 + 동적 메시지 */}
        <div>
          <IdRow>
            <IdInput
              placeholder="아이디"
              value={userId}
              onChange={(e) => onChangeUserId(e.target.value)}
            />
            <CheckButton onClick={onClickCheckId}>
              중복<br />확인
            </CheckButton>
          </IdRow>
          {idChecked && idMsg && (
            <StatusMessage ok={idOK}>{idMsg}</StatusMessage>
          )}
        </div>

        {/* 비밀번호 */}
        <InputBox
          type="password"
          placeholder="비밀번호"
          value={pw}
          onChange={(e) => onChangePw(e.target.value)}
        />

        {/* 비밀번호 확인 + 동적 메시지 */}
        <div>
          <InputBox
            type="password"
            placeholder="비밀번호 확인"
            value={pw2}
            onChange={(e) => onChangePw2(e.target.value)}
          />
          {pw2 && pwMsg && <StatusMessage ok={pwOK}>{pwMsg}</StatusMessage>}
        </div>

        {/* 가입하기 버튼 (메시지 아래 40px) */}
        <SignupButton>가입하기</SignupButton>
      </Form>
    </Container>
  );
}

/* ===== styled-components ===== */

const Container = styled.div`
  width: 393px;
  height: 852px;
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2.5rem 1.25rem;
  margin: 0 auto;
`;

const Title = styled.h1`
  font-size: 3rem; /* 48px */
  color: #4CAF50;
  margin-top: 3.75rem; /* 60px */
`;

const Form = styled.div`
  width: 17.25rem; /* 276px */
  margin-top: 1.875rem; /* 30px */
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.875rem; /* 요소 간 30px */
`;

const InputBox = styled.input`
  width: 17.25rem; /* 276px */
  height: 3rem;    /* 48px */
  box-sizing: border-box;
  border: 0.125rem solid #8F8F8F; /* 2px */
  border-radius: 0.625rem; /* 10px */
  font-size: 1rem;
  padding-left: 1rem;
  outline: none;
`;

const Helper = styled.p`
  margin: 0;
  margin-top: 0.625rem; /* 10px (위 입력창과 간격) */
  font-size: 1rem; /* 16px */
  color: #666;
  text-align: left;
  width: 100%;
`;

const IdRow = styled.div`
  display: flex;
  align-items: center;
  gap: 0.3125rem; /* 5px */
  width: 100%;
`;

const IdInput = styled(InputBox)`
  width: 221px; /* 276 - 버튼 50 - 간격 5 */
  flex: none;
`;

const CheckButton = styled.button`
  width: 50px;
  height: 3rem;
  background-color: #32885D;
  color: #fff;
  font-size: 0.8125rem; /* 13px */
  border: none;
  border-radius: 0.625rem; /* 10px */
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  line-height: 1.1;
  text-align: center;
  cursor: pointer;
`;

/* 동적 메시지 색:
   성공/일치   -> #00CA98
   실패/불일치 -> #FF5656 */
const StatusMessage = styled.p`
  margin: 0;
  margin-top: 0.625rem; /* 10px */
  font-size: 0.9375rem; /* 15px */
  width: 100%;
  text-align: left;
  color: ${({ ok }) => (ok ? "#00CA98" : "#FF5656")};
`;

/* 가입하기 버튼 (비번확인 메시지 아래 40px) */
const SubmitButton = styled.button`
  width: 217px;
  height: 58px;
  background-color: #66CE94;
  color: #fff;
  font-size: 32px;
  border: none;
  border-radius: 100px;
  cursor: pointer;
  margin-top: 30px; /* 여기만 30px로 줄임 */
  display: flex;
  align-items: center;
  justify-content: center;
`;
