// src/pages/modals/namemodal.jsx
import React from "react";
import styled from "styled-components";
import yong from "../../assets/연필조아용.png";

export default function NameModal() {
  return (
    <Wrap>
      <Card>
        <Mascot src={yong} alt="yong" />
        <Title aria-hidden>닉네임 변경 모달</Title>

        <Field>
          <VisuallyHidden htmlFor="nickname">변경할 닉네임</VisuallyHidden>
          <Input id="nickname" placeholder="변경할 닉네임" />
        </Field>

        <Buttons>
          <Btn type="button" variant="ghost">취소</Btn>
          <Btn type="button">변경하기</Btn>
        </Buttons>
      </Card>
    </Wrap>
  );
}

/* 카드를 중앙에 두고, 위로 튀어나올 공간 확보 */
const Wrap = styled.div`
  width: 44rem;              /* 카드 너비와 동일 */
  margin: 6.5rem auto 4rem;  /* ↑ 위로 6.5rem 여백 줘서 잘림 방지 */
`;

const Card = styled.div`
  position: relative;
  width: 100%;
  padding: 3rem 3rem 2.25rem;
  background: #ffffff;
  border-radius: 2rem;
  box-shadow: 0 0.75rem 2rem rgba(0, 0, 0, 0.15);
  overflow: visible; /* 혹시 모를 자식 잘림 방지 */
`;

const Mascot = styled.img`
  position: absolute;
  top: -5rem;
  right: 4.5rem;
  width: 8.5rem;
  height: auto;
  user-select: none;
  pointer-events: none;
`;

const Title = styled.h2`
  margin: 0 0 1.25rem;
  font-size: 1rem;
  font-weight: 600;
  color: #6b6b6b;
`;

const Field = styled.div`
  margin-top: 2.5rem;
`;

const Input = styled.input`
  width: 100%;
  height: 4rem;
  padding: 0 1.5rem;
  font-size: 1.125rem;
  border: 0.125rem solid #9aa0a6;
  border-radius: 1.5rem;
  outline: none;
  ::placeholder { color: #bdbdbd; }
  :focus {
    border-color: #7a8a7f;
    box-shadow: 0 0 0 0.25rem rgba(63, 126, 91, 0.12);
  }
`;

const Buttons = styled.div`
  display: flex;
  gap: 1.25rem;
  margin-top: 2rem;
`;

const Btn = styled.button`
  flex: 1;
  height: 3.5rem;
  border: none;
  border-radius: 2rem;
  font-size: 1.125rem;
  font-weight: 700;
  box-shadow: 0 0.5rem 1rem rgba(0,0,0,0.08);
  cursor: pointer;
  transition: transform 0.05s ease, box-shadow 0.2s ease, opacity 0.2s ease;
  background: ${({ variant }) => (variant === "ghost" ? "#eeeeee" : "#3f7e5b")};
  color: ${({ variant }) => (variant === "ghost" ? "#2f6a4a" : "#ffffff")};
  :active { transform: translateY(0.0625rem); }
`;

const VisuallyHidden = styled.label`
  position: absolute;
  width: 1px; height: 1px; padding: 0; margin: -1px;
  overflow: hidden; clip: rect(0 0 0 0); white-space: nowrap; border: 0;
`;
