// src/pages/SignIn/index.jsx
import React, { useState } from "react";
import styled, { createGlobalStyle } from "styled-components";

// 폰트 파일 임포트
import DNFBitBit from "../../fonts/DNFBitBitTTF.ttf";
import TJJoyM from "../../fonts/TJJoyofsingingM_TTF.ttf";

// 버튼 전용 폰트 등록
const ButtonFont = createGlobalStyle`
  @font-face {
    font-family: 'DNFBitBitTTF';
    src: url(${DNFBitBit}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }
`;

// 기본 폰트 등록
const BaseFont = createGlobalStyle`
  @font-face {
    font-family: 'TJJoyM';
    src: url(${TJJoyM}) format('truetype');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
  }

  body {
    font-family: 'TJJoyM', sans-serif;
  }
`;

// 페이지 컨테이너
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

// 인풋 필드 스타일
const Input = styled.input`
  width: 300px;
  height: 58px;
  border: 1px solid #ccc;
  border-radius: 12px;
  padding: 0 15px;
  font-size: 18px;
  margin-top: ${(props) => props.mt || "30px"};
  font-family: 'TJJoyM', sans-serif;
`;

// 헬퍼 텍스트
const HelperText = styled.div`
  font-size: 14px;
  margin-top: 10px;
  color: ${(props) => props.color || "#000"};
  font-family: 'TJJoyM', sans-serif;
`;

// 중복확인 버튼
const CheckButton = styled.button`
  margin-left: 8px;
  height: 40px;
  background-color: #4CAF50;
  border: none;
  color: white;
  padding: 0 15px;
  border-radius: 8px;
  cursor: pointer;
`;

// 가입하기 버튼 (전용 폰트 적용)
const SignUpButton = styled.button`
  width: 217px;
  height: 58px;
  background-color: #66CE94;
  color: #fff;
  font-size: 32px;
  border: none;
  border-radius: 100px;
  cursor: pointer;
  margin-top: 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: 'DNFBitBitTTF', sans-serif;
`;

export default function SignIn() {
  const [id, setId] = useState("");
  const [pw, setPw] = useState("");
  const [pwCheck, setPwCheck] = useState("");
  const [idHelper, setIdHelper] = useState("");
  const [pwHelper, setPwHelper] = useState("");

  // 아이디 중복확인 예시 로직
  const handleCheckId = () => {
    if (id === "test") {
      setIdHelper({ text: "이미 사용 중입니다!", color: "#FF5656" });
    } else {
      setIdHelper({ text: "사용 가능한 아이디입니다!", color: "#00CA98" });
    }
  };

  // 비밀번호 확인 로직
  const handlePwCheck = (value) => {
    setPwCheck(value);
    if (value && value === pw) {
      setPwHelper({ text: "비밀번호가 일치합니다!", color: "#00CA98" });
    } else if (value && value !== pw) {
      setPwHelper({ text: "비밀번호가 불일치합니다!", color: "#FF5656" });
    } else {
      setPwHelper("");
    }
  };

  return (
    <Container>
      <BaseFont />
      <ButtonFont />

      <div style={{ display: "flex", alignItems: "center" }}>
        <Input
          placeholder="아이디"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        <CheckButton onClick={handleCheckId}>중복 확인</CheckButton>
      </div>
      {idHelper && (
        <HelperText color={idHelper.color}>{idHelper.text}</HelperText>
      )}

      <Input
        placeholder="비밀번호"
        type="password"
        value={pw}
        onChange={(e) => setPw(e.target.value)}
      />

      <Input
        placeholder="비밀번호 확인"
        type="password"
        value={pwCheck}
        onChange={(e) => handlePwCheck(e.target.value)}
      />
      {pwHelper && (
        <HelperText color={pwHelper.color}>{pwHelper.text}</HelperText>
      )}

      <SignUpButton>가입하기</SignUpButton>
    </Container>
  );
}
