import React from "react";
import bgImage from "../assets/signup-bg.png";
import styled from "styled-components";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

export default function AiRecommendation() {
  const cards = [
    {
      title: "힐링할 수 있는 축제",
      subtitle: "모험을 풍선 축제 등 3개",
      name: "풍선 축제",
      date: "2025.07.30 ~ 08.20",
    },
    {
      title: "혼자서도 즐길 수 있는 축제",
      subtitle: "모험을 풍선 축제 등 3개",
      name: "풍선 축제",
      date: "2025.07.30 ~ 08.20",
    },
    {
      title: "조용히 즐길 수 있는 축제",
      subtitle: "모험을 풍선 축제 등 3개",
      name: "풍선 축제",
      date: "2025.07.30 ~ 08.20",
    },
  ];

  const sliderSettings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 1.1, 
    slidesToScroll: 1,
    arrows: false,
    centerMode: false,
    responsive: [
      {
        breakpoint: 768, 
        settings: { slidesToShow: 1.2 }
      },
      {
        breakpoint: 1024,
        settings: { slidesToShow: 2.5 }
      }
    ]
  };

  return (
    <Container>
      {/* <Header>AI 추천</Header>
      {cards.map((card, idx) => (
        <Card key={idx}>
          <Title>{card.title}</Title>
          <Subtitle>{card.subtitle}</Subtitle>
          <ImageSlider>
            <Slider {...sliderSettings}>
              {[1, 2, 3].map((n) => (
                <ImageBox key={n}>
                  <ImagePlaceholder />
                </ImageBox>
              ))}
            </Slider>
          </ImageSlider>
          <CardFooter>
            <FestivalName>{card.name}</FestivalName>
            <Date>{card.date}</Date>
          </CardFooter>
        </Card>
      ))} */}
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  width: 24.5625rem;
  height: 53.25rem;  
  padding: 1rem;
  background: #f7f7f7;
  font-family: sans-serif;
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  margin: 0 auto;
`;

const Header = styled.h2`
  background: #a7e4c5;
  color: white;
  font-weight: bold;
  padding: 0.4rem 0.8rem;
  border-radius: 1rem;
  display: inline-block;
  font-size: clamp(1rem, 2.5vw, 1.25rem);
`;

const Card = styled.div`
  background: #fff;
  border-radius: 1rem;
  padding: 0.8rem;
  margin-top: 1rem;
  box-shadow: 0 0.25rem 0.5rem rgba(0,0,0,0.1);

  @media (min-width: 768px) {
    padding: 1rem;
  }
`;

const Title = styled.h3`
  font-size: clamp(0.9rem, 2vw, 1.1rem);
  margin: 0;
`;

const Subtitle = styled.p`
  font-size: clamp(0.75rem, 1.8vw, 0.9rem);
  color: #666;
  margin: 0.2rem 0 0.6rem 0;
`;

const ImageSlider = styled.div`
  margin-bottom: 0.5rem;

  .slick-list {
    height: auto !important;
  }

  .slick-slide {
    padding-right: 0.5rem;
  }
`;


const ImageBox = styled.div`
  height: clamp(10px, 10vw, 140px);
`;

const ImagePlaceholder = styled.div`
  background: skyblue;
  height: 100%;
  border-radius: 0.8rem;
`;

const CardFooter = styled.div`
  display: flex;
  flex-direction: column;
`;

const FestivalName = styled.span`
  font-weight: bold;
  font-size: clamp(0.85rem, 1.8vw, 1rem);
`;

const Date = styled.span`
  font-size: clamp(0.75rem, 1.5vw, 0.85rem);
  color: #888;
`;
