// src/pages/login.jsx  (버튼 추가/스타일 반영 버전)
import React from "react";
import styled, { createGlobalStyle } from "styled-components";
import bgImage from "../assets/signup-bg.png";
import yong from "../assets/login-yong.png";
import cloud from "../assets/구름.png";
import miniCloud from "../assets/미니구름.png";
import titleImg from "../assets/요기용인.png";

import DNFBitBit from "../fonts/DNFBitBitTTF.ttf";
import JoyM from "../fonts/TJJoyofsingingM_TTF.ttf";

const FontStyles = createGlobalStyle`
  @font-face { font-family:'DNFBitBitTTF'; src:url(${DNFBitBit}) format('truetype'); font-display:swap; }
  @font-face { font-family:'JoyM'; src:url(${JoyM}) format('truetype'); font-display:swap; }
`;

export default function Login() {
  return (
    <Stage>
      <Container>
        <FontStyles />
        <Title src={titleImg} alt="요기용in" />

        {/* 입력 폼 */}
        <Form>
          <InputBox placeholder="아이디를 입력해주세요." />
          <InputBox type="password" placeholder="비밀번호를 입력해주세요." />
        </Form>

        {/* 버튼 그룹 */}
        <Buttons>
          <LoginBtn>로그인</LoginBtn>
          <SignupBtn>회원가입</SignupBtn>
        </Buttons>

        {/* 장식 */}
        <Cloud src={cloud} alt="구름" />
        <MiniCloud src={miniCloud} alt="미니 구름" />
        <Yong src={yong} alt="용" />
      </Container>
    </Stage>
  );
}

/* ===== styles ===== */
const Stage = styled.div`
  min-height: 100vh;
  display: flex; align-items: center; justify-content: center;
`;

const Container = styled.div`
  position: relative;
  width: 24.5625rem;  /* 393px */
  height: 53.25rem;   /* 852px */
  background: url(${bgImage}) no-repeat center center;
  background-size: contain;
  padding: 2.5rem 1.25rem;
  overflow: hidden;
`;

const Title = styled.img`
  position: absolute; top: 6rem; left: 50%; transform: translateX(-50%);
  width: 18rem; height: auto; z-index: 3; pointer-events: none;
`;

const Form = styled.div`
  position: relative; z-index: 3;
  margin-top: 15rem;                      /* 타이틀 밑으로 */
  display: flex; flex-direction: column; align-items: center;
  gap: 1.25rem;                           /* 입력 간격 20px */
`;

const InputBox = styled.input`
  width: 20rem;         /* 320px */
  height: 3.75rem;      /* 60px */
  border: 0.125rem solid #8F8F8F;
  border-radius: 0.625rem;
  font-size: 1.125rem;  /* 18px */
  padding-left: 1rem;
  outline: none;
  font-family: 'JoyM', sans-serif;
  &::placeholder { font-family: 'JoyM', sans-serif; }
`;

/* 폼과 버튼 사이 25px, 버튼끼리 15px */
const Buttons = styled.div`
  position: relative; z-index: 3;
  margin-top: 1.5625rem;   /* 25px */
  display: flex; flex-direction: column; align-items: center;
  gap: 0.9375rem;          /* 15px */
`;

const baseBtn = `
  width: 8.125rem;         /* 130px */
  height: 3rem;            /* 48px */
  border-radius: 6.25rem;  /* 100px */
  border: 0; cursor: pointer;
  display: flex; align-items: center; justify-content: center;
  font-family: 'JoyM', sans-serif; /* 폼과 동일 폰트 */
  font-size: 1.125rem;      /* 폼과 동일 크기 */
  box-shadow: 2px 2px 0 rgba(0,0,0,0.5);
`;


const LoginBtn = styled.button`
  ${baseBtn}
  background: #66CE94;
  color: #fff;
`;

const SignupBtn = styled.button`
  ${baseBtn}
  background: #FCFAF0;
  color: #000;
`;

const Cloud = styled.img`
  position: absolute; left: -1rem; bottom: 0; width: 30rem; z-index: 1;
`;
const MiniCloud = styled.img`
  position: absolute; right: 0.5rem; bottom: 0; width: 13rem; z-index: 1;
`;
const Yong = styled.img`
  position: absolute; left: 0; bottom: 0; width: 100%; height: auto; z-index: 2;
`;
